mapper.insert:
insert into t_merchant_inviter_modify_record

    uid, inviter_uid, inviter_name, old_inviter_uid, old_inviter_name, old_inviter_source, merchant_id, franchisee_id, tenant_id, operator, remark, del_flag, create_time, update_time


    #{uid}, #{inviterUid}, #{inviterName}, #{oldInviterUid}, #{oldInviterName}, #{oldInviterSource}, #{merchantId}, #{franchiseeId}, #{tenantId}, #{operator}, #{remark}, #{delFlag}, #{createTime}, #{updateTime}

mapper.select:
select

from t_merchant_inviter_modify_record
where del_flag = 0 and tenant_id = #{tenantId} and uid = #{uid}
order by id desc

    limit #{offset}, #{size}

select count(1)
from t_merchant_inviter_modify_record
where del_flag = 0
  and tenant_id = #{tenantId}
  and uid = #{uid}
