mapper.insert:
insert into t_merchant_join_record
merchant_id, channel_employee_uid, place_id, inviter_uid, inviter_type, join_uid, start_time, expired_time, status, protection_time, protection_status, del_flag, create_time, update_time, tenant_id, remark, modify_inviter
#{merchantId}, #{channelEmployeeUid}, #{placeId}, #{inviterUid}, #{inviterType}, #{joinUid}, #{startTime}, #{expiredTime}, #{status}, #{protectionTime}, #{protectionStatus}, #{delFlag}, #{createTime}, #{updateTime}, #{tenantId}, #{remark}, #{modifyInviter}

mapper.update:
update t_merchant_join_record


        status = #{status},


        update_time = #{updateTime}

    where join_uid = #{joinUid}

update t_merchant_join_record
set protection_status = #{protectionStatus},
    update_time       = #{updateTime}
where protection_time <= #{updateTime}
  and protection_status = 0

update t_merchant_join_record
set status      = #{status},
    update_time = #{updateTime}
where expired_time <= #{updateTime}
  and status = 1

update t_merchant_join_record
set status      = #{status},
    update_time = #{updateTime}
where id = #{id}

