mapper.select:
SELECT
FROM t_merchant_join_record
limit #{offset}, #{size}

select IFNULL(count(1), 0) from (
select distinct(join_uid) from t_merchant_join_record

    modify_inviter = 0

        and start_time >= #{startTime}


        and start_time <= #{endTime}


        and channel_employee_uid = #{channelEmployeeUid}


        and inviter_type = #{type}


        and inviter_uid = #{uid}


        and status = #{status}


        and tenant_id = #{tenantId}



) a

SELECT merchant_id, count( 1 ) as 'merchantUserNum' FROM t_merchant_join_record WHERE del_flag = 0 and `status` = #{status}
and tenant_id = #{tenantId}
AND merchant_id IN
#{merchantId}
GROUP BY merchant_id

select join_uid,start_time,status from t_merchant_join_record

    del_flag = 0

        and start_time >= #{startTime}


        and start_time <= #{endTime}


        and inviter_type = #{type}


        and inviter_uid = #{uid}


        and status = #{status}


        and tenant_id = #{tenantId}

    order by start_time desc
    limit #{offset}, #{size}

select tmjr.id, tmjr.merchant_id, tmjr.join_uid, tui.name, tui.phone, tubmc.member_card_id as packageId, tubmc.member_card_expire_time as expireTime, tubmc.order_id, tmjr.inviter_uid, tmjr.inviter_type, tmjr.status, tmjr.tenant_id, tmjr.protection_status, tmjr.protection_time from t_merchant_join_record tmjr left join t_user_info tui on tui.uid = tmjr.join_uid left join t_user_battery_member_card tubmc on tmjr.join_uid = tubmc.uid
tmjr.del_flag = 0 and tmjr.status = 2
and tmjr.merchant_id = #{merchantId}
and tmjr.tenant_id = #{tenantId}
and tui.name like concat('%', #{name}, '%')
and tubmc.member_card_expire_time >= #{expireTime}
and tubmc.member_card_expire_time <= #{expireTime} and tubmc.member_card_expire_time >= #{currentTime}
and tubmc.member_card_expire_time <= #{currentTime}
order by tubmc.member_card_expire_time desc limit #{offset}, #{size}

