mapper.update:
update t_merchant
set exist_place_fee = #{existsPlaceFee}, update_time = #{updateTime}
  where id in

      #{id}

sql.where:
del_flag = 0

    and tenant_id = #{tenantId}


    and name like concat('%', concat(#{name}, '%'))


    and franchisee_id = #{franchiseeId}


    and merchant_grade_id = #{merchantGradeId}


    and channel_employee_uid  = #{channelEmployeeUid}


    and id in

        #{id}

mapper.select:
SELECT
FROM t_merchant
del_flag = 0 and uid = #{uid}

SELECT
    count(1)
FROM
    t_merchant
WHERE
    del_flag = 0
    and tenant_id = #{tenantId}
    and name = #{name}

      and id != #{id}

select IFNULL(count(1), 0) from t_merchant

    del_flag = 0

        and tenant_id = #{tenantId}


        and create_time >= #{startTime}


        and create_time < #{endTime}
