mapper.update:
update t_merchant_place_bind
   set type = 1,
    un_bind_time = #{updateTime}

     merchant_id = #{merchantId}
     and type = 0

         and place_id in

             #{placeId}

mapper.select:
select 
from t_merchant_place_bind

    and type = #{status}

where del_flag = 0 and merchant_id = #{merchantId}
