mapper.select:
select month_date , place_id, eid, sn, rent_start_time ,rent_end_time,rent_days,place_fee,month_place_fee,
    tenant_id, del_flag, create_time, update_time
from t_merchant_place_fee_month_record
where month_date = #{date}
  and tenant_id = #{tenantId}
  and del_flag = 0 order by place_id desc

select month_date , place_id, eid, sn, rent_start_time ,rent_end_time,rent_days,place_fee,month_place_fee, tenant_id, del_flag, create_time, update_time from t_merchant_place_fee_month_record where del_flag = 0 and eid != 0
and place_id in
#{placeId}
and month_date in
#{month}
