mapper.delete:
delete from t_merchant_place_map

   merchant_id = #{merchantId}

       and place_id in

           #{placeId}

mapper.select:
SELECT
place_id,
merchant_id
FROM
t_merchant_place_map
where del_flag = 0

    and place_id in

        #{placeId}





        and merchant_id != #{merchantId}



        and merchant_id = #{merchantId}




    and merchant_id in

        #{merchantId}

SELECT place_id, merchant_id FROM t_merchant_place_map m LEFT JOIN t_merchant_place p ON p.id = m.place_id
m.del_flag = 0
and p.franchisee_id = #{franchiseeId}
and m.merchant_id != #{notMerchantId}

SELECT merchant_id, count( 1 ) as 'count' FROM t_merchant_place_map WHERE del_flag = 0 AND merchant_id IN
#{merchantId}
GROUP BY merchant_id
