mapper.select:
SELECT
id,
`name`
FROM
t_merchant_place

    del_flag = 0

        and tenant_id = #{tenantId}



        and franchisee_id = #{franchiseeId}



        AND id IN

            #{id}

SELECT
    count(1)
FROM
    t_merchant_place

    del_flag = 0

        and tenant_id = #{tenantId}


        and name = #{name}


        and id != #{nqId}

sql.where:
del_flag = 0

    and name like concat('%', concat(#{name}, '%'))


    and merchant_area_id = #{merchantAreaId}

mapper.select:
SELECT c.id, c.`name`, b.place_id FROM t_electricity_cabinet c LEFT JOIN t_merchant_place_cabinet_bind b on b.cabinet_id = c.id
c.del_flag = 0 and b.del_flag = 0 and b.`status` = 1 and c.franchisee_id = #{franchiseeId} and c.tenant_id = #{tenantId}
and c.`name` like concat('%', concat(#{cabinetName}, '%'))
ORDER BY c.id desc limit #{offset}, #{size}
