mapper.select:
SELECT
id,
`name`
FROM
t_merchant_place

    del_flag = 0

        and tenant_id = #{tenantId}



        and franchisee_id = #{franchiseeId}



        AND id IN

            #{id}

SELECT
    count(1)
FROM
    t_merchant_place

    del_flag = 0

        and tenant_id = #{tenantId}


        and name = #{name}


        and id != #{nqId}

sql.where:
t.del_flag = 0

    and t.name like concat('%', concat(#{name}, '%'))


    and t.tenant_id = #{tenantId}


    and t.merchant_area_id = #{merchantAreaId}


    and t.franchisee_id = #{franchiseeId}


    and t.id in

        #{id}



    and EXISTS (
    SELECT
    1
    FROM
    t_merchant_place_map m
    WHERE
    m.place_id = t.id
    and m.merchant_id = #{merchantId}
    )

mapper.select:
SELECT c.id as 'cabinetId', c.`name` as 'cabinetName', b.place_id FROM t_electricity_cabinet c LEFT JOIN t_merchant_place_cabinet_bind b ON b.cabinet_id = c.id AND b.del_flag = 0 AND b.`status` = 0
c.del_flag = 0 and c.franchisee_id = #{franchiseeId} and c.tenant_id = #{tenantId}
and c.`name` like concat('%', concat(#{cabinetName}, '%'))
ORDER BY c.id desc limit #{offset}, #{size}

SELECT id, `name` FROM t_merchant_place
tenant_id = #{tenantId}
AND id IN
#{id}
