mapper.select:
select any_value(id) as id, sum(merchant_count) as merchantCount, sum(month_first_total_money) as monthFirstTotalMoney, sum(month_renew_total_money) as monthRenewTotalMoney, sum(month_total_money) as monthTotalMoney, date as date, any_value(tenant_id) as tenantId from t_merchant_promotion_month_record
del_flag = 0
and date = #{monthDate}
and tenant_id = #{tenantId}
and franchisee_id in
#{franchiseeId}
and franchisee_id = #{franchiseeId}
group by date order by date desc
limit #{offset}, #{size}

select count(1)
from (select any_value(id) as id,
sum(merchant_count) as merchantCount,
sum(month_first_total_money) as monthFirstTotalMoney,
sum(month_renew_total_money) as monthRenewTotalMoney,
sum(month_total_money) as monthTotalMoney,
date as date,
any_value(tenant_id) as tenantId
from t_merchant_promotion_month_record

    del_flag = 0

        and date = #{monthDate}


        and tenant_id = #{tenantId}


        and franchisee_id in

            #{franchiseeId}



        and franchisee_id = #{franchiseeId}


group by date
) a
