mapper.select:
select

from t_merchant_promotion_month_record

    del_flag = 0

        and date = #{monthDate}


        and tenant_id = #{tenantId}


order by id desc

    limit #{offset}, #{size}

select count(1)
from t_merchant_promotion_month_record

    del_flag = 0

        and date = #{monthDate}


        and tenant_id = #{tenantId}
