mapper.insert:
insert into t_merchant_withdraw_application
uid, order_no, bank_name, bank_code, bank_number, account_name, amount, remark, status, del_flag, create_time, update_time, tenant_id, franchisee_id
#{uid}, #{orderNo}, #{bankName}, #{bankCode}, #{bankNumber}, #{accountName}, #{amount}, #{remark}, #{status}, #{delFlag}, #{createTime}, #{updateTime}, #{tenantId}, #{franchiseeId}

mapper.select:
select tmwa.id, tmwa.uid, tm.name, tmwa.order_no, tmwa.amount, tmwa.remark, tmwa.status, tmwa.receipt_time, tmwa.tenant_id, tmwa.del_flag, tmwa.create_time, tmwa.update_time from t_merchant_withdraw_application tmwa left join t_user tu on tu.uid = tmwa.uid left join t_merchant tm on tm.uid = tmwa.uid
tmwa.del_flag = 0 and tmwa.tenant_id = #{tenantId}
and tmwa.uid = #{uid}
and tmwa.order_no = #{orderNo}
and tmwa.status = #{status}
and tmwa.create_time >= #{beginTime}
and tmwa.create_time <= #{endTime}
order by tmwa.create_time desc
limit #{offset}, #{size}

