select tmwa.id, tmwa.uid, tmwa.order_no, tmwa.amount, tmwa.remark, tmwa.status, tmwa.receipt_time, tmwa.tenant_id, tmwa.del_flag, tmwa.create_time, tmwa.update_time, tmwa.franchisee_id from t_merchant_withdraw_application tmwa
