select tmwa.id,
    tmwa.uid,
    tm.name,
    tmwa.`order_no`,
    tmwa.amount,
    tmwa.remark,
    tmwa.status,
    tmwa.receipt_time,
    tmwa.tenant_id,
    tmwa.del_flag,
    tmwa.create_time,
    tmwa.update_time,
    tmwa.`check_time`,
    tmwair.`transaction_batch_id`,
    tmwair.`transaction_detail_id`,
    tm.`franchisee_id`
from t_merchant_withdraw_application tmwa
LEFT JOIN `t_merchant_withdraw_application_record` tmwair on tmwair.`order_no` = tmwa.`order_no`
left join t_merchant tm on tm.uid = tmwa.uid

    tmwa.del_flag = 0
    and tmwa.tenant_id = #{tenantId}

        and tmwa.uid = #{uid}


        and tmwa.status = #{status}


        and tmwa.receipt_time  >=   #{beginTime}


        and tmwa.receipt_time  <=   #{endTime}

     AND tmwair.`transaction_batch_id` = #{transactionBatchId} 
     AND tmwair.`transaction_detail_id` = #{transactionDetailId} 
     AND tmwa.`check_time` BETWEEN #{checkTimeStart} AND #{checkTimeEnd} 

        and tm.franchisee_id in

            #{id}



        and tm.franchisee_id = #{franchiseeId}


order by tmwa.create_time desc

    limit #{offset}, #{size}

