mapper.select:
select mwar.id,
       mwar.response,
       mwar.create_time,
       mwar.update_time,
       mwar.tenant_id,
       mw.remark,
       mwar.uid,
       mwar.order_no,
       mwar.transaction_no,
       mwar.status
from t_merchant_withdraw_application_record mwar
left join t_merchant_withdraw_application mw on mwar.order_no = mw.order_no

    mwar.tenant_id = #{tenantId}

        and mwar.id = #{id}



        and mwar.batch_no = #{batchNo}



        and mwar.transaction_no
         =   #{transaction_no}



        and mwar.status = #{status}



        and mwar.create_time  >=   #{beginTime}


        and mwar.create_time  <=   #{endTime}


order by mwar.create_time desc

    limit #{offset}, #{size}

select count(1)
from t_merchant_withdraw_application_record mwar

    mwar.tenant_id = #{tenantId}

        and mwar.id = #{id}



        and mwar.batch_no = #{batchNo}



        and mwar.transaction_no
         =   #{transaction_no}



        and mwar.status = #{status}


        and mwar.create_time  >=   #{beginTime}



        and mwar.create_time  <=   #{endTime}

select mwar.id,
mwar.response,
mwar.create_time,
mwar.update_time,
mwar.tenant_id,
mwar.remark,
mwar.uid,
mwar.order_no,
mwar.transaction_no,
mwar.status
from t_merchant_withdraw_application_record mwar

    mwar.tenant_id = #{tenantId}

        and mwar.id = #{id}



        and mwar.batch_no = #{batchNo}



        and mwar.transaction_no
         =   #{transaction_no}



        and mwar.status = #{status}



        and mwar.create_time  >=   #{beginTime}


        and mwar.create_time  <=   #{endTime}


order by mwar.create_time desc

    limit #{offset}, #{size}

mapper.delete:
update t_merchant_withdraw_application_record
set del_flag = 1
where id = #{id}

mapper.update:
update t_merchant_withdraw_application_record


        remark = #{remark},


        status = #{status},


        update_time = #{updateTime},


where batch_no = #{batchNo}
  and tenant_id = #{tenantId}

update t_merchant_withdraw_application_record


        response = #{response},


        remark = #{remark},


        transaction_batch_id = #{transactionBatchId},



        transaction_detail_id = #{transactionBatchDetailId},


        status = #{status},


        update_time = #{updateTime},




        and batch_no = #{batchNo}



        and batch_detail_no = #{batchDetailNo}



        and tenant_id = #{tenantId}
