mapper.select:
select trc.id, trc.level, trc.mid, trc.channeler_invitation, trc.channeler_renewal, trc.merchant_invitation, trc.merchant_renewal, trc.status, trc.del_flag, trc.franchisee_id, trc.tenant_id, trc.create_time, trc.update_time from t_rebate_config trc left join t_battery_member_card bmc on trc.mid = bmc.id
and trc.mid = #{mid}
and trc.status = #{status}
and trc.level = #{level}
and trc.franchisee_id = #{franchiseeId}
and trc.tenant_id = #{tenantId}
and trc.del_flag = #{delFlag}
order by bmc.franchisee_id,bmc.id desc

select
    id, level, mid, channeler_invitation, channeler_renewal, merchant_invitation, merchant_renewal, status, del_flag, franchisee_id, tenant_id, create_time, update_time
from t_rebate_config WHERE del_flag=0 and mid = #{mid} AND level = #{level}

select
    id, level, mid, channeler_invitation, channeler_renewal, merchant_invitation, merchant_renewal, status, del_flag, franchisee_id, tenant_id, create_time, update_time
from t_rebate_config WHERE del_flag=0 and mid = #{mid}
