mapper.select:
select
id, uid, name, phone, order_id, original_order_id, member_card_id, member_card_name, type, order_type, franchisee_id, level, merchant_id, merchant_uid, status, channeler, channeler_rebate, merchant_rebate, place_id, place_uid, rebate_time, settle_time, tenant_id, create_time, update_time
from t_rebate_record


        and channeler = #{channeler}


        and merchant_id = #{merchantId}


        and franchisee_id = #{franchiseeId}


        and order_id =#{orderId}


        and type = #{type}


        and uid = #{uid}


        and tenant_id = #{tenantId}


        and create_time >= #{beginTime}


        and create_time <= #{endTime}


order by rebate_time desc
limit #{offset}, #{size}

select   count(1) from t_rebate_record


        and channeler = #{channeler}


        and merchant_id = #{merchantId}


        and franchisee_id = #{franchiseeId}


        and order_id =#{orderId}


        and type = #{type}


        and uid = #{uid}


        and tenant_id = #{tenantId}


        and create_time >= #{beginTime}


        and create_time <= #{endTime}

select IFNULL(sum(merchant_rebate), 0) from t_rebate_record


        and tenant_id = #{tenantId}


        and merchant_uid = #{uid}


        and place_uid = #{uid}


        and channeler = #{uid}


        and status = #{status}


        and rebate_time >= #{rebateStartTime}


       and rebate_time <= #{rebateEndTime}


        and settle_time >= #{settleStartTime}


        and settle_time <= #{settleEndTime}

select IFNULL(sum(channeler_rebate), 0) from t_rebate_record


        and tenant_id = #{tenantId}


        and channeler = #{uid}


        and status = #{status}


        and rebate_time >= #{rebateStartTime}


        and rebate_time <= #{rebateEndTime}


        and settle_time >= #{settleStartTime}


        and settle_time <= #{settleEndTime}

select IFNULL(count(1), 0) from t_rebate_record


        and tenant_id = #{tenantId}


        and merchant_uid = #{uid}


        and place_uid = #{uid}


        and channeler = #{uid}


        and status = #{status}


        and type = #{rebateType}


        and create_time >= #{startTime}


        and create_time <= #{endTime}

select
id, uid, name, phone, member_card_id, merchant_id, merchant_uid, status, merchant_rebate, rebate_time
from t_rebate_record


        and merchant_uid = #{uid}


        and place_uid = #{uid}


        and channeler = #{uid}


        and status = #{status}


        and tenant_id = #{tenantId}


        and create_time >= #{startTime}


        and create_time <= #{endTime}


order by id desc
limit #{offset}, #{size}
