mapper.select:
select uid , tenantId , operateType , operateUsername , operateIp , operateContent , operateTime from t_user_operate_log
and operateType = #{query.operateType}
and tenantId = #{query.tenantId}
and uid = #{query.uid}
and operateTime between '${@com.xiliulou.electricity.service.operate.impl.UserOperateLogServiceImpl@convertTime(query.startTime)}' and '${@com.xiliulou.electricity.service.operate.impl.UserOperateLogServiceImpl@convertTime(query.endTime)}'
order by operateTime desc limit #{query.size} offset #{query.offset}

select
count(1)
from t_user_operate_log


        and operateType = #{query.operateType}


        and tenantId = #{query.tenantId}


        and uid = #{query.uid}


        and operateTime between '${@com.xiliulou.electricity.service.operate.impl.UserOperateLogServiceImpl@convertTime(query.startTime)}' and
        '${@com.xiliulou.electricity.service.operate.impl.UserOperateLogServiceImpl@convertTime(query.endTime)}'
