mapper.update:
update t_profit_sharing_order
set

        status = #{status},

   update_time = #{updateTime}
where id = #{id}

mapper.select:
select
from t_profit_sharing_order where del_flag = 0 and id in
#{id}

select

from t_profit_sharing_order
where del_flag=0 and tenant_id=#{tenantId}
and third_order_no in

    #{thirdOrderNo}

SELECT

from t_profit_sharing_order
where del_flag=0
and tenant_id=#{tenantId}
and id>#{startId}
and status in

    #{status}

order by id asc
limit #{size}
