mapper.select:
select
  id, tenant_id, franchisee_id, statistics_type, statistics_time, total_amount, del_flag, create_time, update_time
from t_profit_sharing_statistics


        and id = #{id}


        and tenant_id = #{tenantId}


        and franchisee_id = #{franchiseeId}


        and statistics_type = #{statisticsType}


        and statistics_time = #{statisticsTime}


        and total_amount = #{totalAmount}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_profit_sharing_statistics


        and id = #{id}


        and tenant_id = #{tenantId}


        and franchisee_id = #{franchiseeId}


        and statistics_type = #{statisticsType}


        and statistics_time = #{statisticsTime}


        and total_amount = #{totalAmount}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}
