mapper.update:
update

set third_order_no = #{thirdOrderNo}, update_time=#{updateTime}
where id =#{id}

mapper.select:
select

from

where del_flag = 0 and state = #{state} and id > #{startId}
and channel=#{channel}
and tenant_id=#{tenantId}

    and create_time > #{startTime}


    and third_order_no is not null

order by id asc
limit #{size}

select
    r.third_order_no,
    franchisee_id,
    r.id
from t_profit_sharing_trade_mixed_order r
where r.tenant_id = #{tenantId}
  and r.create_time > #{startTime}
  and r.create_time < #{endTime}
  and r.id > #{startId}
  and exists (select 1 from t_profit_sharing_order_detail d where  d.third_trade_order_no = r.third_order_no  and d.unfreeze_status = 0 )
 limit  #{size}
