mapper.select:
select
  id, tenant_id, franchisee_id, third_merchant_id, order_no, third_order_no, order_type, amount, process_state, remark, channel, rent_rebate, pay_time, del_flag, create_time, update_time
from t_profit_sharing_trade_order


        and id = #{id}


        and tenant_id = #{tenantId}


        and franchisee_id = #{franchiseeId}


        and third_merchant_id = #{thirdMerchantId}


        and order_no = #{orderNo}


        and third_order_no = #{thirdOrderNo}


        and order_type = #{orderType}


        and amount = #{amount}


        and process_state = #{processState}


        and remark = #{remark}


        and channel = #{channel}


        and rent_rebate = #{rentRebate}


        and pay_time = #{payTime}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from t_profit_sharing_trade_order


        and id = #{id}


        and tenant_id = #{tenantId}


        and franchisee_id = #{franchiseeId}


        and third_merchant_id = #{thirdMerchantId}


        and order_no = #{orderNo}


        and third_order_no = #{thirdOrderNo}


        and order_type = #{orderType}


        and amount = #{amount}


        and process_state = #{processState}


        and remark = #{remark}


        and channel = #{channel}


        and rent_rebate = #{rentRebate}


        and pay_time = #{payTime}


        and del_flag = #{delFlag}


        and create_time = #{createTime}


        and update_time = #{updateTime}
