mapper.update:
update t_profit_sharing_trade_order
set process_state = #{processState},

        third_order_no = #{thirdOrderNo},

    update_time = #{updateTime}
where id = #{id}

mapper.select:
select
id,third_order_no
from

where del_flag=0
and tenant_id=#{tenantId}
and process_state=#{processState}
and id > #{startId}
order by id asc
limit #{size}

select

from

where del_flag=0
and tenant_id=#{tenantId}
and third_order_no in

    #{thirdOrderNo}
