mapper.delete:
delete from t_user_info_group_detail where uid = #{uid}

    and group_no in

        #{item}

mapper.select:
select

from t_user_info_group_detail
where group_no = #{groupNo} and uid = #{uid} and tenant_id = #{tenantId}

select any_value(tuigd.id) as id, any_value(tuigd.group_no) as groupno, tuigd.uid as uid, any_value(tuigd.franchisee_id) as franchiseeid, any_value(tuigd.tenant_id) as tenantid, any_value(tuigd.del_flag) as delflag, any_value(tuigd.create_time) as createtime, any_value(tuigd.update_time) as updatetime, any_value(tuigd.operator) as operator, any_value(tuig.id) as groupid, any_value(tuig.name) as groupname from t_user_info_group_detail tuigd left join t_user_info_group tuig on tuig.group_no = tuigd.group_no
tuigd.tenant_id = #{tenantId}
and tuigd.uid = #{uid}
and tuig.id = #{groupId}
and tuigd.franchisee_id in
#{franchiseeId}
group by tuigd.uid order by any_value(tuigd.update_time) desc; limit #{offset}, #{size}

select count(distinct(uid))
from t_user_info_group_detail tuigd
left join t_user_info_group tuig on tuig.group_no = tuigd.group_no

    tuigd.tenant_id = #{tenantId}

        and tuigd.uid = #{uid}


        and tuig.id = #{groupId}


        and tuigd.franchisee_id in

            #{franchiseeId}

select count(distinct (group_no))
from t_user_info_group_detail
where uid = #{uid}

select tuigd.uid as uid, tuigd.group_no as groupNo, tuig.id as groupId, tuig.name as groupName
from t_user_info_group_detail tuigd
left join t_user_info_group tuig on tuigd.group_no = tuig.group_no

    tuig.del_flag = 0 and tuigd.uid = #{uid}

        and tuigd.tenant_id = #{tenantId}


        and tuigd.franchisee_id in

            #{franchiseeId}



order by tuigd.update_time desc, tuigd.id asc

    limit #{offset}, #{size}
