mapper.select:
select tuig.id as id, tuig.name as groupName, count(distinct (tuigd.uid)) as userCount, tuig.franchisee_id as franchiseeId, tuig.operator as operator, tuig.update_time as updateTime, tuig.create_time as createTime from t_user_info_group tuig left join t_user_info_group_detail tuigd on tuig.group_no = tuigd.group_no
tuig.tenant_id = #{tenantId} and tuig.del_flag = 0
and tuig.id = #{groupId}
and tuig.franchisee_id in
#{franchiseeId}
group by tuig.id order by tuig.id desc limit #{offset}, #{size}

select count(1) from t_user_info_group

    tenant_id = #{tenantId} and del_flag = 0

        and id = #{groupId}


        and franchisee_id in

            #{franchiseeId}

select id, name, group_no from t_user_info_group
tenant_id = #{tenantId} and del_flag = 0
and name like concat('%', #{groupName}, '%')
and franchisee_id in
#{franchiseeId}
order by id desc limit #{offset}, #{size}

select

from t_user_info_group
where del_flag = 0 and id = #{id}
